CREATE OR REPLACE FUNCTION TRS_GET_JSON_TOKEN_FNC(      P_JSON  VARCHAR2,      P_KEY   VARCHAR2  )     RETURN    VARCHAR2     AUTHID CURRENT_USER  IS  /*******************************************************************************     1. OBJECT NAME      : TRS_GET_JSON_TOKEN_FNC     2. VERSION          : 1.0     3. CREATE DATE      : 2016.04.26     4. SUB SYSTEM       : TRS     5. AUTHOR           :      6. DESCRIPTION      : JSON KEY의 VALUE를 리턴  *******************************************************************************/      V_JSON    VARCHAR2(1000);      V_VALUE   VARCHAR2(100);      START_POS NUMBER;      END_POS   NUMBER;  BEGIN      V_JSON := TRIM(P_JSON);      START_POS := INSTR(V_JSON, P_KEY);      END_POS   := INSTR(SUBSTR(V_JSON, INSTR(V_JSON, P_KEY)), ',')-1;      IF END_POS < 0 THEN         END_POS := INSTR(SUBSTR(V_JSON, INSTR(V_JSON, P_KEY)), '}')-1;      END IF;            V_VALUE := SUBSTR(V_JSON, START_POS, END_POS);      V_VALUE := SUBSTR(V_VALUE, INSTR(V_VALUE, '=')+1);        RETURN V_VALUE;  END TRS_GET_JSON_TOKEN_FNC;