CREATE OR REPLACE FUNCTION TRS_JOIN_FNC/*******************************************************************************    1. Object Name      : TRS_JOIN_FNC    2. Version          : 1.0    3. Create Date      : 2012.01.14    4. Sub System       : Transportation   5. Author           : Sun, Choi   6. Description      : TRS_JOIN_FNC : 조회 결과 , 사용하여 ROW 로 변경 조회   7. Revision History : 2012.01.14  1.0  Created *******************************************************************************/ (          i_cursor SYS_REFCURSOR,          i_del VARCHAR2 := ','      ) RETURN VARCHAR2       AUTHID CURRENT_USER      IS          v_value   VARCHAR2(32767);          v_result  VARCHAR2(32767);                  BEGIN          LOOP              FETCH i_cursor INTO v_value;                            EXIT WHEN i_cursor%NOTFOUND;                            IF v_result IS NOT NULL THEN                 v_result := v_result || i_del;              END IF;                            v_result := v_result || v_value;          END LOOP;                    CLOSE i_cursor;                            RETURN v_result;                    EXCEPTION         WHEN OTHERS THEN          CLOSE i_cursor;            END TRS_JOIN_FNC;