    /**                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
     * Sheet관련 프로세스 처리 <br>                                                                                                                                                                                                                                                                                                                                                                                                                                                 
     * <br><b>Example :</b>                                                                                                                                                                                                                                                                                                                                                                                                                                                         
     * <pre>                                                                                                                                                                                                                                                                                                                                                                                                                                                                        
     *     doActionIBSheet(sheetObj, document.form, IBSEARCH)                                                                                                                                                                                                                                                                                                                                                                                                                       
     * </pre>                                                                                                                                                                                                                                                                                                                                                                                                                                                                       
     * @param {ibsheet} sheetObj 필수 IBSheet Object                                                                                                                                                                                                                                                                                                                                                                                                                                
     * @param {form} formObj 필수 html form object                                                                                                                                                                                                                                                                                                                                                                                                                                  
     * @param {int} sAction 필수 프로세스 플래그 상수                                                                                                                                                                                                                                                                                                                                                                                                                               
     * @return 없음                                                                                                                                                                                                                                                                                                                                                                                                                                                                 
     * @author 김재연                                                                                                                                                                                                                                                                                                                                                                                                                                                               
     * @version 2009.07.28                                                                                                                                                                                                                                                                                                                                                                                                                                                          
     */                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
     function doActionIBSheet(sheetObj,formObj,sAction) {                                                                                                                                                                                                                                                                                                                                                                                                                           
    	 sheetObj.ShowDebugMsg = false;                                                                                                                                                                                                                                                                                                                                                                                                                                             
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
    	 switch(sAction) {                                                                                                                                                                                                                                                                                                                                                                                                                                                          
    	 	                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
        	case IBSEARCH:      //조회                 
        		ComOpenWait(true);                                                                                                                                                                                                                                                                                                                                                                                                                                          
        		if (!validateForm(sheetObj, formObj, sAction)) {                                                                                                                                                                                                                                                                                                                                                                                                            
        			ComOpenWait(false);                                                                                                                                                                                                                                                                                                                                                                                                                                 
        			return false;                                                                                                                                                                                                                                                                                                                                                                                                                                       
				}                                                                                                                                                                                                                                                                                                                                                                                                                                                   
                
	        	formObj.f_cmd.value = SEARCH01;                
                
				var sXml = sheetObj.GetSearchXml("ESM_PRI_4011GS.do", FormQueryString(formObj));                                                                                                                                                                                                                                                                                                                                                                    
				var arrDesc = ComPriXml2Array(sXml, "flt_pct_tp_cd");                                                                                                                                                                                                                                                                                                                                                                                               
				if (arrDesc != null && arrDesc.length > 0) {                                                                                                                                                                                                                                                                                                                                                                                                        
					setFltPctTpCd(sheetObj, arrDesc[0]);      
				}                                                                                                                                                                                                                                                                                                                                                                                                                                                   
				                                                                                                                                                                                                                                                                                                                                                                                                                                                    
        		formObj.f_cmd.value = SEARCH02;       
        		sheetObj.DoSearch("ESM_PRI_4011GS.do", FormQueryString(formObj));                                                                                                                                                                                                                                                                                                                                                                                           
        		ComOpenWait(false);                                                                                                                                                                                                                                                                                                                                                                                                                                         
        		break;                                                                                                                                                                                                                                                                                                                                                                                                                                                      
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
        	case IBDOWNEXCEL: //download excel                                                                                                                                                                                                                                                                                                                                                                                                                                  
				sheetObj.Down2Excel(-1);                                                                                                                                                                                                                                                                                                                                                                                                                            
				break;         
				
        	case IBCREATE:      // File DownLoad 상태조회
                
    	        if (!validateForm(sheetObj,formObj,sAction)) {
    	            return false;
    	        }
            
            	try {
            		formObj.jb_id.value = "";
    		        sheetObj.WaitImageVisible = false;
    		        ComOpenWait(true);
    		        formObj.f_cmd.value = COMMAND01;
    		    	
    		    	var sXml = sheetObj.GetSearchXml("ESM_PRI_4015GS.do", FormQueryString(formObj));
    		        var backEndJobKey = ComGetEtcData(sXml, "BackEndJobKey");
    		        if (backEndJobKey.length > 0) {
    		            formObj.jb_id.value = backEndJobKey;
                        sheetObj.RequestTimeOut = 10000;
                        timer = setInterval(getBackEndJobStatus, 3000); // 3초 후에
    		        }else{
                        ComOpenWait(false);
                    }
            	}catch(e){
                    ComOpenWait(false);
                }    
    	        break;					
        }                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
    }                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    
