    /**
     * date 검색 조건의 from - to range를 check 한다.
     */
    function chkDatePeriod(formObj) {
    	//eff_date_from eff_date_to access_date
        var form = document.form;
        var fromVal = form.eff_date_from.value.replace(/-/g,'');
        var toVal = form.eff_date_to.value.replace(/-/g,'');

        var fromAddM = ComGetDateAdd(fromVal, "D", 62, "", true);
        if( parseInt(toVal,10) > parseInt(fromAddM,10) ) {
            ComShowCodeMessage("PRI00308", "check the date range.", "Maximum date range is 2 months"); // 'Please {?msg1} {?msg2}.';
            event.returnValue = false;
            ComSetFocus(formObj);
            return false;
        }
        return true;
    }
    
    /**
     * exp dt 검색 조건이 To date가 From date 보다 큰 경우를 체크한다.
     */
    
    function chkDate(formObj) {
    	//eff_date_from eff_date_to access_date
        var form = document.form;
        var fromVal = form.eff_date_from.value.replace(/-/g,'');
        var toVal = form.eff_date_to.value.replace(/-/g,'');
        
        if(fromVal != "" && toVal != "") {
            if( parseInt(fromVal,10) > parseInt(toVal,10) ) {
                //msgs['PRI00305'] = '{?msg1} start date can not be greater than end date.';
                ComShowCodeMessage("PRI00305", "'Effective Date'");  //'{?msg1} start date can not be greater than end date.';
                event.returnValue = false;
                ComSetFocus(formObj);
                return false;
            }
        }
        return true;
    }
