     /** 
      * BackEndJob 관련 Status='3' 이 될때까지 확인한다. <br>
      */ 
     function getBackEndJobStatus() {
     	var sheetObj = sheetObjects[0];
     	var formObj = document.form;
         try {
         		formObj.f_cmd.value = SEARCH;
         		var f_cmd = formObj.f_cmd.value;
     			var jb_id = formObj.jb_id.value;
     			var sParam = "f_cmd="+f_cmd+"&jb_id="+jb_id;
     	        var sXml = sheetObj.GetSearchXml("ESM_PRI_4015GS.do", sParam);
     	        var jobState = ComGetEtcData(sXml, "jb_sts_flg");
     	        if (jobState == "3") {
     	            formObj.f_cmd.value = COMMAND02;
     	            var f_cmd = formObj.f_cmd.value;
     				var jb_id = formObj.jb_id.value;
     				var sParam = "f_cmd="+f_cmd+"&jb_id="+jb_id;
                     document.location.href = "ESM_PRI_4015DL.do?" + sParam;
                     document.onreadystatechange = function() {
                         if (document.readyState == "interactive") {
                             ComOpenWait(false);
                         }
                     }
                     clearInterval(timer);
                     
     	        } else if (jobState == "4") { // BackEndJob을 실패 하였습니다.
     	            ComShowCodeMessage("PRI00338"); //msgs['PRI00338'] = 'Failed to download. Please try again.';
     	            clearInterval(timer);	
     	            ComOpenWait(false);	
     	        } else if (jobState == "5") {
     	            ComShowCodeMessage("PRI00339"); //msgs['PRI00339'] = 'Data was downloaded successfully.';
     	            clearInterval(timer);
     	            ComOpenWait(false);	
     	        }
         }catch(e){
             ComOpenWait(false);
         }
     }

     /** 
      * BackEndJob의 결과가 완료되면 Excel파일로 내려받음.(Request Expense Inital) <br>
      */ 
     function getBackEndJobLoadFile() {
     	var formObj = document.form;
     	var sheetObj = sheetObjects[0];
     	try {
     		formObj.f_cmd.value = SEARCHLIST;
     		var sXml = sheetObj.GetSearchXml("ESM_PRI_4015GS.do", FormQueryString(formObj));
     		sheetObj.LoadSearchXml(sXml);
     		ComOpenWait(false);	
     	}catch(e){
     		ComOpenWait(false);		
     	}
     }     
